(525, 'EZ-Pages Display Status - HeaderBar', 'EZPAGES_STATUS_HEADER', '1', 'Display of EZ-Pages content can be Globally enabled/disabled for the Header Bar<br />0 = Off<br />1 = On<br />2= On ADMIN IP ONLY located in Website Maintenance<br />NOTE: Warning only shows to the Admin and not to the public', 30, 10, NULL, '2012-07-23 17:16:23', NULL, 'zen_cfg_select_option(array(''0'', ''1'', ''2''), '),
(526, 'EZ-Pages Display Status - FooterBar', 'EZPAGES_STATUS_FOOTER', '1', 'Display of EZ-Pages content can be Globally enabled/disabled for the Footer Bar<br />0 = Off<br />1 = On<br />2= On ADMIN IP ONLY located in Website Maintenance<br />NOTE: Warning only shows to the Admin and not to the public', 30, 11, NULL, '2012-07-23 17:16:23', NULL, 'zen_cfg_select_option(array(''0'', ''1'', ''2''), '),
(527, 'EZ-Pages Display Status - Sidebox', 'EZPAGES_STATUS_SIDEBOX', '1', 'Display of EZ-Pages content can be Globally enabled/disabled for the Sidebox<br />0 = Off<br />1 = On<br />2= On ADMIN IP ONLY located in Website Maintenance<br />NOTE: Warning only shows to the Admin and not to the public', 30, 12, NULL, '2012-07-23 17:16:23', NULL, 'zen_cfg_select_option(array(''0'', ''1'', ''2''), '),
(528, 'EZ-Pages Header Link Separator', 'EZPAGES_SEPARATOR_HEADER', '', 'EZ-Pages Header Link Separator<br />Default = &amp;nbsp;::&amp;nbsp;', 30, 20, '2012-10-07 10:17:21', '2012-07-23 17:16:23', NULL, 'zen_cfg_textarea_small('),
